import React from "react";
import {useFormik} from "formik";
// TODO: import useFormik from formik library

function App() {
  const formik = useFormik({
    initialValues: {
      name: " ",
      email: " ",
      password: " "
    },
    onSubmit: values => {
      console.log("form:", values);
    },
    validate: values => {
      let errors = {};
      if(!values.name) {
        errors.name = "Required";
      }
      if(!values.email) {
        errors.email = "Required";
      }
      if(!values.password) {
        errors.password = "Required";
      }
    }
  })

  return (
    <div>
      <form onsubmit={formik.handleSubmit}>
        <div>Name</div>
        <input name="name" type="text" onChange={formik.handleChange}
        value={formik.values.name}/>
        {formik.errors.name && <div style={{color:"red"}}>{formik.errors.name}</div> }
        <div>Email</div>
        <input name="email" type="text" onChange={formik.handleChange}
        value={formik.values.email}/>
        {formik.errors.email && <div style={{color:"purple"}}>{formik.errors.email}</div> }
        <div>Name</div>
        <input name="password" type="text" onChange={formik.handleChange}
        value={formik.values.password}/>
        {formik.errors.password && <div style={{color:"green"}}>{formik.errors.password}</div> }
        <button type="submit">Submit</button>

   </form>
    </div>

    );
}

export default App;
